import { useState } from 'react'

export default function Player () {
    const [player1Edit, setPlayer1Edit] = useState({name: 'Player 1', edit: false})
    const [player2Edit, setPlayer2Edit] = useState({name: 'Player 2', edit: false})

    function playernameEditAndSave (player) {
        if (player === 'player1') {
            console.log('here')
            setPlayer1Edit((previosData) => {return { ...previosData, edit: !previosData.edit}})
        } else {
            setPlayer2Edit(previosData => !previosData.edit)
        }

    }

    return (
        <div>
            <div>
                <label>Player 1</label>
                {player1Edit.edit ? <input value={player1Edit.name} type="string" /> : <span>{player1Edit.name}</span>}
                <button onClick={() => playernameEditAndSave('player1')}>Edit</button>
                <button onClick={() => playernameEditAndSave('player1')}>Save</button>
            </div>
            <div>
                <label>Player 2</label>
                {player2Edit.edit ? <input value={player2Edit.name} type="string" /> : <span>{player1Edit.name}</span>}
                <button onClick={() => playernameEditAndSave('player2')}>Edit</button>
                <button onClick={() => playernameEditAndSave('player2')}>Save</button>
            </div>
        </div>
    )
}
